const buyButtons=document.querySelectorAll(".catalog-detail .buy"),inBasketModal=document.querySelector(".modal-in-basket"),basket=document.querySelector(".basket-link"),modalClose=inBasketModal.querySelector(".modal-close");buyButtons.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),inBasketModal.classList.add("modal-show"),basket.classList.add("added")})}),modalClose.addEventListener("click",e=>{e.preventDefault(),inBasketModal.classList.remove("modal-show"),basket.classList.remove("added")}),document.addEventListener("keydown",e=>{e.preventDefault(),"Escape"===e.key&&(inBasketModal.classList.contains("modal-show")&&(e.preventDefault(),inBasketModal.classList.remove("modal-show")),basket.classList.contains("added")&&(e.preventDefault(),basket.classList.remove("added")))});
